import React from 'react';
import { connect } from 'react-redux';
import { signIn, signOut } from '../actions';

class GoogleAuth extends React.Component {

    componentDidMount(){
        window.gapi.load('client:auth2', ()=>{
            window.gapi.client.init({
                clientId: '4729191079-v30pg37q6uv4bh1b8t685o578bk9k7on.apps.googleusercontent.com',
                scope: 'email'
            }).then(()=> {
                this.auth = window.gapi.auth2.getAuthInstance();
                this.onAuthChange(this.auth.isSignedIn.get())
                this.auth.isSignedIn.listen(this.onAuthChange);
            });
        });
    }

    onAuthChange= (isSignedIn)=>{
        if(isSignedIn)
            this.auth.signIn();
        else
            this.auth.signOut();
    }

    renderAuthButton(){
        
        if(this.auth.isSignedIn=== null)
            return null;
        else if(this.props.isSignedIn)
            return (
                <button onClick={this.props.signOut()} className="ui red google button">
                    <i className="google icon" />
                        Sign Out
                </button>   
            );
        else
            return (
                
                <button  onClick={this.props.signIn()} className="ui red google button">
                    <i className="google icon" />
                        Sign In With Google
                </button>   
            );
    }

    render(){
        return (
            <div>{this.renderAuthButton()}</div>
        );
    }
}

const mapStateToProps= (state)=>{
    console.log(state.auth.isSignedIn);
    return {isSignedIn: state.auth.isSignedIn};
}

export default connect(mapStateToProps, {signIn, signOut})(GoogleAuth);
